@import url('https://fonts.googleapis.com/css2?family=Open+Sans&display=swap');



$maingreen: #00b289;
$maingray: #4c4d5f;

* {
    padding: 0;
    margin: 0;
    outline: none;
    font-family: 'open sans', sans-serif;
}

header {
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-between;
    align-items: center;
    padding: 15px 20px;
    border-bottom: 1px solid #4c4d5f69;
    section#smaller-logo-container {
        width: max-content;
    }
    section#container {
        button#sign-up {
            cursor: pointer;
            color: $maingray;
            font-size: 1rem;
            border: none;
            background-color: transparent;
        }
    }
}
main {
    padding: 10px;
    height: 560px;
    display: flex;
    flex-flow: column nowrap;
    justify-content: center;
    align-items: center;
    > section {
        max-width: 320px;
        width: 75%;
        background-color: white;
        box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.086);
        display: flex;
        border-radius: 5px;
        flex-flow: column nowrap;
        justify-content: center;
        align-items: center;
        padding: 70px 40px;
        gap: 5px;
        > label {
            font-size: 1.5rem;
        }
        > section {
            display: flex;
            flex-flow: row nowrap;
            justify-content: space-between;
            align-items: center;
            width: 100%;
            height: 100px;
            > p {
                width: 100px;
                text-align: center;
                font-size: .7rem;
                color: #4c4d5f59;
            }
            > hr {
                width: 100%;
                background-color: #4c4d5f2f;
                border: none;
                height: 1px;
            }
        }
        > form {
            width: 100%;
            display: flex;
            flex-flow: column nowrap;
            justify-content: center;
            align-items: center;
            gap: 20px;
            input:not(input[type='checkbox']) {
                width: 100%;
                height: 30px;
                border-radius: 5px;
                border: 1px solid #4c4d5f3f;
                padding: 5px 0px 5px 10px;
                &::placeholder {
                    color: #4c4d5f5d;
                }
                &:hover {
                    border-color: $maingreen;
                }
            }
            > section#input-wrapper {
                display: flex;
                flex-flow: row-reverse nowrap;
                justify-content: center;
                align-items: center;
                gap: 10px;
                font-size: .9rem;
                input {
                    cursor: pointer;
                }
            }
            > button {
                cursor: pointer;
            }
            > button[type='submit'] {
                background-color: $maingreen;
                border: none;
                border-radius: 3px;
                width: 100%;
                height: 45px;
                color: white;
                font-size: .9rem;
                font-weight: bold;
                &:hover {
                    background-color: #00bc90;
                }
            }
            > button[type="reset"] {
                color: $maingreen;
                background-color: transparent;
                font-size: .9rem;
                border: none;
            }
        }
    }
}
footer {
    padding: 20px;
    display: flex;
    flex-flow: column nowrap;
    justify-content: center;
    gap: 20px;
    padding-bottom: 40px;
    margin-top: 150px;
    > ul {
        display: flex;
        flex-flow: column nowrap;
        gap: 20px;
        list-style-type: none;
        li {
            font-size: .8rem;
            color: #4c4d5f6d;
            font-weight: bold;
        }
    }
    > p {
        > small, a {
            color: #4c4d5f49;
            font-size: .7rem;
        }
    }
}

@media screen and (min-width: 450px) {
    main {
        height: 650px;
    }
}

@media screen and (min-width: 768px) {
    main {
        align-items: flex-end;
        padding: 10px 20px;
    }
    footer {
        > ul {
            flex-direction: row;
            justify-content: center;
        }
        > p {
            text-align: center;
        }
    }
}

@media screen and (min-width: 1024px) {
    main {
        height: 900px;
        padding: 0px 50px;
    }
    footer {
        margin-top: 80px;
    }
}
