body {
    div#background-shadow {
        //background-image: linear-gradient(65deg, white 70%, #f2faff 50%);
        width: 100%;
        height: 1000px;
        position: absolute;
        left: 0;
        z-index: -20;
    }
}

main {
    section#plans-pricing {
        flex-direction: row;
        justify-content: center;
        height: max-content;
        width: fit-content;
        align-items: flex-end;
        margin-bottom: 50px;
        h2 {
            width: max-content;
            padding: 0;
            white-space: nowrap;
        }
        section#months-free-switch {
            width: max-content;
            div#monthly-switch {
                justify-content: space-between;
                width: 250px;
            }
        }
    }
}

@media (min-width: 490px) {
    main {
        > section#plans-pricing {
            gap: 30px;
        }
        > section#your-business-section {
            flex-direction: row;
            justify-content: space-around;
            p {
                height: max-content;
            }
        }
    }
}


@media (min-width: 768px) {
    main {
        padding-top: 20px;
        > section#plans-pricing {
            gap: 50px;
            h2 {
                font-size: 2rem;
                font-weight: bold;
                display: flex;
                align-items: end;
                height: max-content;
                padding-bottom: 5px;
            }
        }
        > section.super-charge {
            > article#sub-title {
                h2 {
                    font-size: 1.7rem;
                }
            }
            > section#add-ons-article-container {
                > article.add-ons-article {
                    width: 90%;
                    flex-direction: row-reverse;
                    justify-content: space-between;
                    align-items: center;
                    > h2 {
                        height: 60px;
                        font-size: 1.9rem;
                        width: 90px;
                    }
                    > section.trial-price2 {
                        width: 90px;
                    }
                }
            }
        }
        > section#your-business-section {
            background-color: #f2faff;
            border-radius: 5px;
            box-shadow: 2px 2px 2px #f2faff;
        }
    }
}
